describe("get last letter",function(){it("should get the last letter of a string",function(){expect(lastLetter("hello")).toEqual("o"),expect(lastLetter("island")).toEqual("d")})}),describe("squaring",function(){it("should square the input number",function(){expect(square(3)).toEqual(9),expect(square(5)).toEqual(25)})}),describe("negate",function(){it("take a single number arg and return its negative value",function(){expect(negate(5)).toEqual(-5),expect(negate(-8)).toEqual(8)})}),describe("Makes array",function(){it("takes three arguments and returns an array with each of those args as items",function(){expect(toArray(1,4,5)).toEqual([1,4,5]),expect(toArray(8,9,10)).toEqual([8,9,10])})}),describe("startsWithA",function(){it("takes a single string argument and returns true if the given string's first letter is 'A' and false otherwise.",function(){expect(startsWithA("aardvark")).toEqual(!0),expect(startsWithA("bear")).toEqual(!1)})}),describe("excite",function(){it("takes a single string argument and returns the given string plus three exclamation marks.",function(){expect(excite()).toEqual("yes!!!"),expect(excite()).toEqual("no!!!")})}),describe("sun",function(){it("takes a single string argument and returns true if the string contains the word 'sun' within it.",function(){expect("sundries").toEqual(!0),expect("asunder").toEqual(!0),expect("catapult").toEqual(!1)})}),describe("tiny",function(){it("takes a single number argument and returns true if the number is between 0 and 1.",function(){expect(.3).toEqual(!0),expect(14).toEqual(!1),expect(-5).toEqual(!1)})}),describe("getSeconds",function(){it("takes a single string argument in the format 'MM:SS' (hours, minutes, and seconds) and returns the total number of seconds represented by that timespan.",function(){expect("01:30").toEqual(90),expect("10:25").toEqual(625)})});